		if($this->db->trans_status() === FALSE){
			$this->db->trans_rollback();
			$Arr_Kembali	= array(
				'pesan'		=>'Edit Estimation failed. Please try again later ...',
				'status'	=> 2
			);
		}
		else{
			$this->db->trans_commit();
			$Arr_Kembali	= array(
				'id_bq'		=> $data['id_bq'],
				'pembeda'	=> $data['url_help'],
				'pesan'		=>'Edit Estimation Success. Thank you & have a nice day ...',
				'status'	=> 1
			);
			history('Edit Estimation colar slongsong in final drawing code : '.$data['id_bq'].' to '.$data['id_milik'].' product: '.$data['id_product']);
		}
		echo json_encode($Arr_Kembali);
	}
	
	
